// 思路2：
// 事实上，从开始建立string dict的时候就已经输了
// 学会用map！学会用map！学会用map！重要的事情说三遍！
// 而且，每个map仅需保存此字符的上一重复位置
// 该位置的最大值即为以此位结尾的最长串的起始位置

class Solution {
public:
    int lengthOfLongestSubstring(string s) {
        int len = s.length();
        int res = 0;
        map<char,int> finder;
        int repeatPos = -1;
        
        for (int i = 0; i < len; i++)
        {
            if (finder.count(s[i])!=0 && repeatPos < finder[s[i]])
                repeatPos = finder[s[i]];
            
            if (i-repeatPos > res)
                res = i - repeatPos;
            
            finder[s[i]]=i;
        }
        return res;
    }
};

